import sys
sys.setrecursionlimit(10000)


try:
    with open("Bioinfo algorithm/Chapter_5/rosalind_ba5c.txt", "r") as f:
        content = f.read()
except FileNotFoundError:
    content = '''
'''

seq1, seq2 = content.split()[0], content.split()[1]

def LCSlength(X, Y):
    m, n = len(X), len(Y)

    # b for arrow, c for value
    b = [[0 for _ in range(n+1)] for _ in range(m+1)]
    c = [[0 for _ in range(n+1)] for _ in range(m+1)]

    for i in range(1, m+1): c[i][0] = 0
    for j in range(1, n+1): c[0][j] = 0

    # X = ' '+X
    # Y = ' '+Y
    for i in range(1, m+1):
        for j in range(1, n+1):
            if X[i-1] == Y[j-1]:
                c[i][j] = c[i-1][j-1] + 1
                b[i][j] = "d" # diagonal
            elif c[i-1][j] >= c[i][j-1]:
                c[i][j] = c[i-1][j]
                b[i][j] = "u" # up
            else:
                c[i][j] = c[i][j-1]
                b[i][j] = "l" # left
    return b

def PrintLCS(b, X, i, j):
    if i==0 or j==0: return
    elif b[i][j] == "d":
        PrintLCS(b, X, i-1, j-1)
        print(X[i-1], end = "")
    elif b[i][j] == "u":
        PrintLCS(b, X, i-1, j)
    else:
        PrintLCS(b, X, i, j-1)

b = LCSlength(seq1, seq2)
PrintLCS(b, seq1, len(seq1), len(seq2))
